import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

function TodoList({ todo, todos, setTodos }) {
  const handleDelete = (todo) => {
    axios
      .delete(`http://localhost:3001/todos/${todo.id}`)
      .then((res) => {
        const newTodos = todos.filter((el) => el.id !== todo.id);
        setTodos(newTodos);
      })
      .catch((err) => console.error());
  };

  return (
    <>
      <List key={todo.id}>
        <Todo>{todo.todo}</Todo>
        <Date>{todo.date}</Date>
        <EditBtn>수정</EditBtn>
        <DeleteBtn
          onClick={() => {
            handleDelete(todo);
          }}
        >
          삭제
        </DeleteBtn>
      </List>
    </>
  );
}

export default TodoList;

const List = styled.div`
  display: flex;
  margin: auto;
  width: 500px;
  padding: 10px 10px;
  border-radius: 10px;
  border: 1px solid rgba(0, 0, 0, 0.1);
`;

const Todo = styled.p`
  font-size: 20px;
`;
const Date = styled.p`
  font-size: 15px;
`;

const Button = styled.button`
  padding: 10px;
  border: none;
  cursor: pointer;
`;

const EditBtn = styled(Button)``;

const DeleteBtn = styled(Button)`
  background-color: red;
  color: white;
`;
