import axios from 'axios';
import { useState } from 'react';
import styled from 'styled-components';

function TodoList({ todo, todos, setTodos }) {
  const [open, setOpen] = useState(false);
  const [editTodo, setEditTodo] = useState(null);
  console.log(editTodo);

  const deleteTodo = (todo) => {
    axios
      .delete(`http://localhost:3001/todos/${todo.id}`)
      .then((res) => {
        setTodos(todos.filter((el) => el.id !== todo.id));
      })
      .catch((err) => console.error());
  };

  const updateTodo = () => {
    console.log('UPDATE!');
    // axios.put(`http://localhost:3001/todos/${todo.id}`);
  };

  const EditForm = () => {
    return (
      <>
        <form>
          <input type='text' value={editTodo.todo} onChange={(event) => setEditTodo({ ...editTodo, todo: event.target.value })}></input>
        </form>
        <button onClick={setEditTodo(null)}>수정완료</button>
      </>
    );
  };

  return (
    <>
      <List key={todo.id}>
        {editTodo !== null ? <EditForm /> : <Todo>{todo.todo}</Todo>}
        {/* <Date>{todo.date}</Date> */}
        <ButtonArea>
          <EditBtn onClick={() => setEditTodo()}>수정</EditBtn>
          <DeleteBtn
            onClick={() => {
              deleteTodo(todo);
            }}
          >
            삭제
          </DeleteBtn>
        </ButtonArea>
      </List>
    </>
  );
}

export default TodoList;

const List = styled.li`
  display: flex;
  justify-content: space-between;
  margin: auto;
  width: 500px;
  padding: 10px 10px;
  border-radius: 10px;
  border: 1px solid rgba(0, 0, 0, 0.1);
`;

const Todo = styled.span`
  font-size: 20px;
`;
const Date = styled.span`
  font-size: 15px;
`;

const ButtonArea = styled.div``;

const Button = styled.button`
  padding: 10px;
  border: none;
  cursor: pointer;
`;

const EditBtn = styled(Button)``;

const DeleteBtn = styled(Button)`
  background-color: red;
  color: white;
`;
