import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');

  const getTodos = () => {
    axios('http://localhost:3001/todos').then((res) => setTodos(res.data));
  };

  useEffect(() => {
    getTodos();
  }, [newTodo]);

  const handleNewTodo = (event) => {
    event.preventDefault();
    if (!newTodo) return;

    const data = {
      id: todos.length ? todos[todos.length - 1].id + 1 : 1,
      todo: newTodo,
      completed: false,
    };

    axios.post('http://localhost:3001/todos', data).then((res) => setTodos([...todos, res.data]));
    setNewTodo('');
  };

  const handleDelete = (todo) => {
    axios
      .delete(`http://localhost:3001/todos/${todo.id}`)
      .then((res) => {
        const newTodos = todos.filter((el) => el.id !== todo.id);
        setTodos(newTodos);
      })
      .catch((err) => console.log(err));
  };

  return (
    <>
      <form onSubmit={handleNewTodo}>
        <Input type='text' placeholder='입력해주세요' value={newTodo} onChange={(event) => setNewTodo(event.target.value)} />
        <button></button>
      </form>
      {todos.map((todo) => (
        <TodoBoard key={todo.id}>
          <Todo>{todo.todo}</Todo>
          <DeleteBtn
            onClick={() => {
              handleDelete(todo);
            }}
          >
            X
          </DeleteBtn>
        </TodoBoard>
      ))}
    </>
  );
}

// console.log(`server is working on ${process.env.REACT_APP_BACKEND_URL}`);

export default TodoList;

const TodoBoard = styled.div`
  display: flex;
  justify-content: center;
  text-align: center;
  border: 1px solid rgba(0, 0, 0, 0.1);
  margin: 3vh;
  padding: 10px;
`;

const Input = styled.input`
  padding: 10px;
  border-radius: 10px;
  border: 1px solid rgba(0, 0, 0, 0.1);
`;

const Todo = styled.p``;

const DeleteBtn = styled.button`
  background-color: red;
  color: white;
  padding: 5px;
  border: none;
  border-radius: 50%;
  cursor: pointer;
`;
