import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import TodoList from './components/TodoList';

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');

  const getTodos = () => {
    axios('http://localhost:3001/todos').then((res) => setTodos(res.data));
  };

  useEffect(() => {
    getTodos();
  }, [newTodo]);

  const handleNewTodo = (event) => {
    event.preventDefault();

    const data = {
      id: todos.length ? todos[todos.length - 1].id + 1 : 1,
      todo: newTodo,
      completed: false,
    };
    axios
      .post('http://localhost:3001/todos', data)
      .then((res) => setTodos([res.data, ...todos]))
      .catch((err) => console.error());
    setNewTodo('');
  };

  return (
    <div className='App'>
      <header>
        <h1>My Todo List üîñ</h1>
      </header>
      <form>
        <Input value={newTodo} type='text' placeholder='üéâ Ïó¨Í∏∞Îã§ Ï†ÅÏñ¥Îùº' onChange={(event) => setNewTodo(event.target.value)} />
        <Button disabled={!newTodo} type='submit' onClick={handleNewTodo}>
          Îì±Î°ùÌïòÍ∏∞
        </Button>
      </form>
      <ul>
        {todos.map((todo) => (
          <TodoList todo={todo} todos={todos} setTodos={setTodos} />
        ))}
      </ul>
    </div>
  );
}

export default App;

const Input = styled.input`
  padding: 10px;
  border-radius: 10px;
  border: 1px solid rgba(0, 0, 0, 0.1);
`;
