import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');

  const getTodos = () => {
    axios('http://localhost:3001/todos').then((res) => setTodos(res.data));
  };

  useEffect(() => {
    getTodos();
  }, [newTodo]);

  const handleNewTodo = (event) => {
    event.preventDefault();
    if (!newTodo) return;

    const data = {
      id: todos.length ? todos[todos.length - 1].id + 1 : 1,
      todo: newTodo,
      completed: false,
    };
    axios.post('http://localhost:3001/todos', data).then((res) => setTodos([res.data, ...todos]));
    setNewTodo('');
  };

  const handleDelete = (todo) => {
    axios
      .delete(`http://localhost:3001/todos/${todo.id}`)
      .then((res) => {
        const newTodos = todos.filter((el) => el.id !== todo.id);
        setTodos(newTodos);
      })
      .catch((err) => console.log(err));
  };

  return (
    <>
      <form onSubmit={handleNewTodo}>
        <input type='text' placeholder='add' value={newTodo} onChange={(event) => setNewTodo(event.target.value)} />
      </form>
      {todos.map((todo) => (
        <TodoBoard key={todo.id}>
          <p>{todo.todo}</p>
          <button
            onClick={() => {
              handleComplete(todo);
            }}
          >
            ✅
          </button>
          <button
            onClick={() => {
              handleDelete(todo);
            }}
          >
            ❌
          </button>
        </TodoBoard>
      ))}
    </>
  );
}

console.log(`server is working on ${process.env.REACT_APP_BACKEND_URL}`);

export default TodoList;

const TodoBoard = styled.div`
  border: 1px solid black;
  background-color: aliceblue;
  margin: 3vh;
  padding: 10px;
  display: flex;
  justify-content: center;
`;
