import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

function TodoList({ todo }) {
  const handleNewTodo = (event) => {
    event.preventDefault();

    const data = {
      id: todos.length ? todos[todos.length - 1].id + 1 : 1,
      todo: newTodo,
      completed: false,
    };
    axios
      .post('http://localhost:3001/todos', data)
      .then((res) => setTodos([res.data, ...todos]))
      .catch((err) => console.error());
    setNewTodo('');
  };

  const handleDelete = (todo) => {
    axios
      .delete(`http://localhost:3001/todos/${todo.id}`)
      .then((res) => {
        const newTodos = todos.filter((el) => el.id !== todo.id);
        setTodos(newTodos);
      })
      .catch((err) => console.error());
  };

  return (
    <>
      {todos.map((todo) => (
        <TodoBoard key={todo.id}>
          <Todo>{todo.todo}</Todo>
          <EditBtn>Edit</EditBtn>
          <DeleteBtn
            onClick={() => {
              handleDelete(todo);
            }}
          >
            Delete
          </DeleteBtn>
        </TodoBoard>
      ))}
    </>
  );
}

// console.log(`server is working on ${process.env.REACT_APP_BACKEND_URL}`);

export default TodoList;

const TodoBoard = styled.div`
  display: flex;
  justify-content: space-between;
  border: 1px solid rgba(0, 0, 0, 0.1);
  padding: 2vh 4vh;
  margin: 3vh;
`;

const Input = styled.input`
  padding: 10px;
  border-radius: 10px;
  border: 1px solid rgba(0, 0, 0, 0.1);
`;

const Todo = styled.p`
  font-size: 20px;
`;

const Button = styled.button`
  padding: 10px;
  border: none;
  cursor: pointer;
`;

const EditBtn = styled(Button)``;

const DeleteBtn = styled(Button)`
  background-color: red;
  color: white;
`;
