import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import TodoList from './components/TodoList';
import uuid from 'react-uuid';

function App() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');

  useEffect(() => {
    axios('http://localhost:3001/todos').then((res) => setTodos(res.data));
  }, [newTodo]);

  const addNewTodo = (event) => {
    event.preventDefault();

    const today = new Date();
    const data = {
      id: uuid(),
      todo: newTodo,
      date: today.toLocaleDateString(),
    };

    axios
      .post('http://localhost:3001/todos', data)
      .then((res) => setTodos([...todos, res.data]))
      .catch((err) => console.error());

    setNewTodo('');
  };

  return (
    <div className='App'>
      <header>
        <h1>🔖 My Todo List</h1>
      </header>
      <form>
        <Input value={newTodo} type='text' placeholder='🎉 할 일을 입력해주세요' onChange={(event) => setNewTodo(event.target.value)} />
        <Button disabled={!newTodo} type='submit' onClick={addNewTodo}>
          +
        </Button>
      </form>
      <ul>
        {todos.map((todo) => (
          <TodoList todo={todo} todos={todos} setTodos={setTodos} />
        ))}
      </ul>
    </div>
  );
}

export default App;

const Input = styled.input`
  width: 500px;
  padding: 10px;
  border-radius: 10px;
  border: 1px solid rgba(0, 0, 0, 0.5);
  &:focus {
    outline: none;
    border: none;
    box-shadow: 0px 0px 5px orange;
  }
`;

const Button = styled.button`
  padding: 10px;
  border: 1px solid rgba(0, 0, 0, 0.5);
  border-radius: 10px;
  cursor: pointer;
`;
