import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');

  const getTodos = () => {
    axios('http://localhost:3001/todos').then((res) => setTodos(res.data));
  };

  useEffect(() => {
    getTodos();
  }, [newTodo]);

  const handleNewTodo = (event) => {
    event.preventDefault();
    if (!newTodo) return;

    const data = {
      id: todos.length ? todos[todos.length - 1].id + 1 : 1,
      todo: newTodo,
      completed: false,
    };
    axios.post('http://localhost:3001/todos', data).then((res) => setTodos([res.data, ...todos]));
    setNewTodo('');
  };

  const handleDelete = (todo) => {
    console.log(todo);
  };

  return (
    <>
      <form onSubmit={handleNewTodo}>
        <input type='text' placeholder='add' value={newTodo} onChange={(event) => setNewTodo(event.target.value)} />
      </form>
      {todos.map((todo) => (
        <TodoPlate key={todo.id}>
          <p>{todo.todo}</p>
          <button
            onClick={() => {
              handleDelete(todo);
            }}
          >
            ‚ùå
          </button>
        </TodoPlate>
      ))}
    </>
  );
}

console.log(`server is working on ${process.env.REACT_APP_BACKEND_URL}`);

export default TodoList;

const TodoPlate = styled.div`
  display: flex;
  justify-content: center;
`;
